package MyPackage;

public class MySwitch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 5;
		
		switch(x)
		{
		case 0:
			System.out.println("Case 0: small");
			break;
		case 1:
			System.out.println("Case 1: Medium");
			break;
		case 2:
			System.out.println("Case 2: Large");
			break;
		case 3:
			System.out.println("Case 3: x-large");
			break;
		default:
			System.out.println("Default: xx-large");
			break;
		}
	
	}

}


	
